var result;

try {
	let configTable = me.GetConfigurationTable({
		tableName: "ComponentManager" /* STRING */
	});

	if (configTable && configTable.length > 0) {
		configTable.rows.toArray().forEach(row => {
			if (row.id == "ComponentManager") {
				result = row.managerThingName;
			}
		});
	}

	let projectName = me.GetProjectName();
	if (!result && projectName != "PTC.Base") {
		let abstractProjectName;
		try {
			abstractProjectName = me.GetAbstractComponentBuildingBlockName();
		} catch (err) {
			logger.warn("{} - {}:{} - {}. Could not retrieve abstract project name, defaulting current project Name [{}]", me.name, err.fileName, err.lineNumber, err, projectName);
			abstractProjectName = projectName;
		}
		let identifier = abstractProjectName + ".Manager";
		let baseManager = Things["PTC.Base.EntryPoint"].GetConfiguredComponentManager();
		result = Things[baseManager].GetGlobalDefaultManagerForIdentifier({
			uid: identifier
		});
	}
} catch (err) {
	logger.error("{} - {}:{} - {}", me.name, err.fileName, err.lineNumber, err);
	throw ("An error has occurred. Please check the script logs for more details.");
}


